mapper.select:
select * from t_user_role,
    (select * from t_user where username=#{username} and password=#{password}) user
        where t_user_role.role_id=1 and user.id=t_user_role.user_id;

select *,t_role.id rid from t_user_role,t_role,
              (select *,id uid from t_user where username like "%${searchName}%" limit #{startIndex},#{pageSize}) user
where user.id=t_user_role.user_id and t_user_role.role_id=t_role.id;
